import logo from './logo.svg';
//import './App.css';
import BarChart from './Charts/BarChart';
import Piechart from './Charts/PieChart';
import DoughnutChart from './Charts/DoughnutChart';
import LineChart from './Charts/LineChart';
import "./styles.css";
import React, { useState, useEffect } from 'react'


function App() {
  const [selectedComponent, setSelectedComponent] = useState('');

  const handleComponentChange = (event) => {
    setSelectedComponent(event.target.value);
  };
  return (

    <div className="chartCard">
      <div className="chartBox">
      <BarChart />
      <label htmlFor="component-select">Select a component:</label>
      <select id="component-select" value={selectedComponent} onChange={handleComponentChange}>
        <option value="">Select...</option>
        <option value="Barchart"><BarChart/></option>
        <option value="Piechart"><Piechart/></option>
        <option value="Linechart"><LineChart/></option>
        <option value="Doughnutchart"><DoughnutChart/></option>
      </select>
      </div>

      {selectedComponent && (
        <div>
          <h2>Selected Component: {selectedComponent}</h2>
          {/* Render the selected component based on the value */}
          {selectedComponent === 'Barchart' && <BarChart />}
          {selectedComponent === 'Piechart' && <Piechart />}
          {selectedComponent === 'Linechart' && <LineChart />}
          {selectedComponent === 'Doughnutchart' && <DoughnutChart />}
        </div>
      )}


    </div>
  );
}

export default App;
