import React, { useState, useEffect } from 'react'
import {
  Chart as ChartJS,

  BarElement,
  CategoryScale,
  LinearScale,

} from 'chart.js';

import { Bar } from 'react-chartjs-2';

ChartJS.register(
    CategoryScale,
    LinearScale,
  BarElement,
);


const BarChart = () => {
  //const [chart, setChart] = useState({})
  var baseUrl = "https://www.carboninterface.com/api/v1/vehicle_makes";
  //var proxyUrl = "https://cors-anywhere.herokuapp.com/";
  var apiKey = "Bearer kIcSmE5I0hdwTn6bS9PmA";

  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchCoins = async () => {
      try {
        const response = await fetch(`${baseUrl}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `${apiKey}`,
            'Access-Control-Allow-Origin': "*"
          }
        });
        const json = await response.json();
        const limitedData = json.slice(0, 7); // Limiting the data to 10 items
        setData(limitedData);
      } catch (error) {
        console.error(error);
      }
    };

    fetchCoins();
  }, [baseUrl, apiKey]);
//   useEffect(() =>
//   {
//     const fetchCoins = async () => {
//       await fetch(`${baseUrl}`, {
//         method: 'GET',
//         headers: {
//           'Content-Type': 'application/json',
//           'Authorization': `${apiKey}`,
//           'Access-Control-Allow-Origin': "*"
//         }
//       })
//       .then(response => response.json())
//      .then(json => setData(json))
//      .catch(error => console.error(error));
//   };
//   fetchCoins()
// }, [baseUrl,apiKey]);



  console.log("data", data);
  var mdata = {
    labels: data?.map(x => x.data.attributes.name),
    datasets: [{
      label: `${data?.length} `,
      data: data?.map(x => x.data.attributes.number_of_models),
      backgroundColor: [
        'rgba(255, 99, 132, 0.2)',
        'rgba(54, 162, 235, 0.2)',
        'rgba(255, 206, 86, 0.2)',
        'rgba(75, 192, 192, 0.2)',
        'rgba(153, 102, 255, 0.2)',
        'rgba(255, 159, 64, 0.2)'
      ],
      borderColor: [
        'rgba(255, 99, 132, 1)',
        'rgba(54, 162, 235, 1)',
        'rgba(255, 206, 86, 1)',
        'rgba(75, 192, 192, 1)',
        'rgba(153, 102, 255, 1)',
        'rgba(255, 159, 64, 1)'
      ],
      borderWidth: 1
    }]
  };

  var options = {
    maintainAspectRatio: false,
    scales: {
        x:{
            title :{
                display:'true',
                text : 'Vehicle Make'
            }
        },
        y:{
            title :{
                display:'true',
                text : 'No of Vehicles'
            }
        }
    },
    legend: {
      labels: {
        fontSize: 25,
      },
    },
  }

  return (
    <div>
      <Bar
        data={mdata}
        height={400}
        options={options}

      />
    </div>
  )
}

export default BarChart
