import React, { useState, useEffect } from 'react'
import {
  Chart as ChartJS,

  BarElement,

} from 'chart.js';

import { Bar } from 'react-chartjs-2';


ChartJS.register(
  BarElement,
);


const BarChart = () => {
  const [chart, setChart] = useState({})
  var baseUrl = "https://api.collegefootballdata.com/games?year=2001&week=1&seasonType=regular";
  //var proxyUrl = "https://cors-anywhere.herokuapp.com/";
  var apiKey = "Bearer HluUuDw/Rs6augHRl87Xr9W0L0xby7ZtcCAcd3Nv72NRxRDlL2XAeQKgfrpuvIYo";


//   useEffect(() => {
//     const fetchCoins = async () => {
//       await fetch(`${baseUrl}`, {
//         method: 'GET',
//         headers: { 
//           'Content-Type': 'application/json',
//           'Authorization': `${apiKey}`,
//           'Access-Control-Allow-Origin': "*"
//         }
//       })
//         .then((response) => {
//           if (response.ok) {
//             response.json().then((json) => {
//               console.log(json.data);
//               setChart(json.data)
//             });
//           }
//         }).catch((error) => {
//           console.log(error);
//         });
//     };
//     fetchCoins()
//   }, [baseUrl,apiKey])



  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`${baseUrl}`, {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `${apiKey}`,
          },
        }).then((response) => {
            if (response.ok) {
              response.json().then((json) => {
                console.log(json.data);
                setChart(json.data)
              });
            }
          })
        // Handle the response data here
      } catch (error) {
        // Handle any errors that occur during the request
        console.log(error);
      }
    };

    fetchData();
  }, [baseUrl,apiKey]);



  console.log("chart", chart);

  var data = {
    labels: chart?.map(x => x.venue),
    datasets: [{
      label: `${chart?.coins?.length} Coins Available`,
      data: chart?.map(x => x.attendance),
      backgroundColor: [
        'rgba(255, 99, 132, 0.2)',
        'rgba(54, 162, 235, 0.2)',
        'rgba(255, 206, 86, 0.2)',
        'rgba(75, 192, 192, 0.2)',
        'rgba(153, 102, 255, 0.2)',
        'rgba(255, 159, 64, 0.2)'
      ],
      borderColor: [
        'rgba(255, 99, 132, 1)',
        'rgba(54, 162, 235, 1)',
        'rgba(255, 206, 86, 1)',
        'rgba(75, 192, 192, 1)',
        'rgba(153, 102, 255, 1)',
        'rgba(255, 159, 64, 1)'
      ],
      borderWidth: 1
    }]
  };

  var options = {
    maintainAspectRatio: false,
    scales: {
    },
    legend: {
      labels: {
        fontSize: 25,
      },
    },
  }

  return (
    <div>
      <Bar
        data={data}
        height={400}
        options={options}

      />
    </div>
  )
}

export default BarChart
