import React, { useState, useEffect } from 'react'
import {
  Chart as ChartJS,

  BarElement,
  CategoryScale,
  LinearScale,

} from 'chart.js';

import { Bar } from 'react-chartjs-2';


ChartJS.register(
    CategoryScale,
    LinearScale,
  BarElement,
);


 const BarChart = () => {
 const [chart, setChart] = useState({})
  var baseUrl = "https://isro.vercel.app/api/customer_satellites";
//   //var proxyUrl = "https://cors-anywhere.herokuapp.com/";
//   //var apiKey = "Bearer kIcSmE5I0hdwTn6bS9PmA";




// useEffect(() => {
//    const fetchData = async() => { 
//     fetch('baseUrl')
//       .then(response => response.json())
//       .then(json => setChart(json))
//       .catch(error => console.error(error));
//   }, []);


useEffect(() => {
    const fetchCoins = async () => {
      await fetch(`${baseUrl}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      })
        .then((response) => {
          if (response.ok) {
            response.json().then((json) => {
              console.log(json.data);
              setChart(json.data)
            });
          }
        }).catch((error) => {
          console.log(error);
        });
    };
    fetchCoins()
  }, [baseUrl])




console.log("chart", chart);


  if (chart !== undefined && chart !== null) {
  var data = {
    //labels: chart?.customer_satellites?.map(x => x.launcher),
    datasets: [{
      label: `${chart?.customer_satellites?.length} Coins Available`,
      data: chart?.customer_satellites?.map(x => x.mass),
      backgroundColor: [
        'rgba(255, 99, 132, 0.2)',
        'rgba(54, 162, 235, 0.2)',
        'rgba(255, 206, 86, 0.2)',
        'rgba(75, 192, 192, 0.2)',
        'rgba(153, 102, 255, 0.2)',
        'rgba(255, 159, 64, 0.2)'
      ],
      borderColor: [
        'rgba(255, 99, 132, 1)',
        'rgba(54, 162, 235, 1)',
        'rgba(255, 206, 86, 1)',
        'rgba(75, 192, 192, 1)',
        'rgba(153, 102, 255, 1)',
        'rgba(255, 159, 64, 1)'
      ],
      borderWidth: 1
    }]
  };
}

  var options = {
    maintainAspectRatio: false,
    scales: {
    },
    legend: {
      labels: {
        fontSize: 25,
      },
    },
  }

  return (
    <div>
      <Bar
        data={data}
        height={400}
        options={options}

      />
    </div>
  )
}

 export default BarChart
